function selectwin = mkselectwin(coords,redux,screenrect,images)% rect = [left top right bottom]if nargin>3	names = fieldnames(images);	scrw = screenrect(3);	scrh = screenrect(4);	selectwin = zeros(scrh,scrw);	for i=1:length(names)		eval(['currimage = images.',names{i},';']);		currimage = imresize(currimage,redux);		selectwin(coords(i,2)+1:coords(i,4),coords(i,1)+1:coords(i,3))=currimage;	endendreturn;			