function [ptrs,rects] = openoffscreenwindows(scrs,colors,inrects)% function [ptrs,rects] = openoffscreenwindows(scrs,colors,inrects)%% opens an arbitrary number of off screen windows% SCRS is a vector containing the the screen numbers that the windows% correspond toif nargin	windowPtrs=SCREEN('Windows');	screenNumbers=SCREEN(windowPtrs,'WindowScreenNumber')	badlocs = find(scrs,screenNumbers);	if isempty(badlocs)		if nargin == 1			for i = 1:length(scrs)				[ptrs(i),rects(:,i)]=SCREEN(scrs(i),'OpenOffscreenWindow');			end		elseif nargin == 2 			if length(scrs)==length(colors)				for i = 1:length(scrs)					[ptrs(i),rects(:,i)]=SCREEN(scrs(i),'OpenOffscreenWindow',colors(i));				end			else				printstr('number of screens and colors don''t match: ');				ptrs = [];				rects = [];				return;			end								elseif nargin == 3			if length(scrs)==length(colors) & length(scrs)==size(inrects,1)								for i = 1:length(scrs)					[ptrs(i),rects(:,i)]=SCREEN(scrs(i),'OpenOffscreenWindow',colors(i),inrects(i,:));				end			else				printstr('number of screens and/or colors and/or rects don''t match: ');				ptrs = [];				rects = [];				return;			end		end			else		printstr(['illegal screen number(s): ',num2str(badlocs)]);		ptrs = [];		rects = [];		return;	end		else	printstr('no screen numbers passed');	ptrs = [];	rects = [];endreturn;	