function [s] = placeimages(letters,images,lettersize,maxrows,maxcols,c,uncertainty)% function [s] = placeimages(letters,images,lettersize,maxrows,maxcols,c)if uncertainty	k = 0;	for i = 0:maxrows-1		for j = 0:maxcols-1			k = k+1;			coords(k,:) = [i*lettersize + 1, j*lettersize + 1];		end	end	seq = randperm(maxrows*maxcols);	s=zeros(maxrows*lettersize,maxcols*lettersize);	for i = 1:length(letters)		eval(['s(coords(seq(i),1):coords(seq(i),1)+lettersize-1,coords(seq(i),2):coords(seq(i),2)+lettersize-1) = images.',letters(i),'*sqrt(c/std(images.'letters(i),'(:))^2);']);	endelse	s = [];	for i = 1:length(letters)		eval(['s = [s,images.',letters(i),'*sqrt(c/std(images.'letters(i),'(:))^2)];']);	endendreturn;