function t = showoffscrmovie(offscrPtrs,mainscrPtr,cluts,frames)oldBoolean=SCREEN(mainscrPtr,'Preference','SetClutWaitVBL',1);%loop = 'for i = 1:length(offscrPtrs), SCREEN(mainscrPtr,''SetClut'',cluts(:,(i-1)*3+1:i*3),0); SCREEN(mainscrPtr,''WaitVBL'',frames(i)-1); screen(''CopyWindow'',offscrPtrs(i),mainscrPtr);end';loop = 'for i = 1:length(offscrPtrs), tic; SCREEN(mainscrPtr,''SetClut'',cluts(:,(i-1)*3+1:i*3),0); SCREEN(mainscrPtr,''WaitVBL'',frames(i)-1); screen(''CopyWindow'',offscrPtrs(i),mainscrPtr);t(i)=toc;end';%Rush(loop,1);eval(loop);return;