function amkdir(name,folder)% function amkdir(name,folder)% % uses applescript to make a new directory named 'name' in the% folder 'folder'.%% adapted from function 'acopy'.%% July 14, 1997  JMG U of T Vision Labif ~exist([folder,name],'dir')	folder = ['"' cvttofullname(folder) '"'];	name = ['"',name,'"'];	resultString = applescript('amkdir.as', '-useEnglish','itemName', name, 'folderName', folder);	resultString = resultString(2:end-1); % strip off excess quotes	disp(resultString);else	printstr('Folder already exists.');end