function [void] = ce(varargin)% function [void] = ce(varargin)%% Clears all variables in the main workspace EXCEPT those% passed.  Any number of variables can be passed. Just invoking% the function alone clears all of the variables. Non-existent% variables passed are ignored.%% June 26, 1998   JMG   U of T Vision Lab% Get the variables in the main workspace.allvars = evalin('base','who');% If no parameters were passed, clear everything.if size(varargin) == 0	evalin('base','clear all');% Otherwise, clear only those not passed.	else	clearVector = zeros(length(allvars));	for j = 1:length(varargin)		for i = 1:length(allvars)			if size(allvars{i}) == size(varargin{j})				if  allvars{i} == varargin{j}					clearVector(i) = 1;					break;				end			end		end	end		for i = 1:length(allvars)		if clearVector(i) == 0			evalin('base',['clear ',allvars{i}]);		end	endend	return