% Convert a filename to a full path namefunction fullname = cvttofullname(partialOrFullName)%   Copyright (c) 1984-97 by The MathWorks, Inc.%   $Revision: 1.3 $  $Date: 1997/04/10 20:59:19 $if any(partialOrFullName == filesep)	if partialOrFullName(1) == filesep        fullname = fullfile(cd, partialOrFullName);    else        fullname = partialOrFullName;    endelse	fullname = fullfile(cd, partialOrFullName);end