function openapp(name,folder)% function openapp(file,folder)% % uses applescript to open an application. if the name and folder% are not passed, a dialog box allows the user to select the application.%% adapted from the function 'acopy'.%% July 14, 1997  JMG U of T Vision Labif nargin < 2	[name,folder] = uigetfile('*.*','Choose Application');endif ~isempty(name)	name = ['"',name,'"'];	folder = ['"' cvttofullname(folder) '"'];	resultString = applescript('openapp.as', '-useEnglish','appname', name,'appfolder',folder);	resultString = resultString(2:end-1); % strip off excess quotes	disp(resultString);end