function printstr(str,fid);% function printstr(str,fid);% % print a string with a carriage return to the specified% file of file id 'fid'. if fid is not passed, the string% is printed to the command window with a carriage return.% if a number is passed for str, it is converted into a string.% printstr alone produces a carriage return in the command% window.%% July 1 1998 JMG U of T Vision Labif exist('str','var')	if ~isstr(str)		str = num2str(str);	endelse	str = [];endif exist('fid','var')	fprintf(fid,[str,'\n']);else	fprintf(1,[str,'\n']);endreturn