function setpath(file,name)% function setpath(file,name)%% Use with jg: jg setp <pathname>% General use: setpath <masterfile> <pathname>%% sets the current path to a stored path 'name'.%% July 16, 1998  JMG U of T Vision Lab% choose the switch file 'jg.m' if no file is passedmd=pwd;if nargin < 1	file = 'jg.m';ends=findstr('.m',file);eval([file(1:s-1),' env;']);% save the path if it existsif exist('name','var')		if exist([name,'.mat'],'file');		eval(['load ',name]);		evalin('base','oldpath=path;save oldpath oldpath;');		eval(['path(',name,');']);		printstr('Previous path saved as global variable and file ''oldpath''.');	else		printstr('Path file not found.');	endelse	printstr('No file name passed.');endcd(md);return