function [ params ] = logfitfn( data, init )% function [ params ] = logfitfn( data, init )%% fits a log function to data. data are independant and dependant% data, stored in a 2 column matrix. init are the initial values for% the parameters. third col is optional weights.%% May 1998  JMG U of T Vision Labdefarg('init',[10 10 10]);sz = size(data);if sz(2) == 2	data = [data,ones(sz(1),1)];end% data(:,3) = (data(:,3)).^(-1);% functionerrfn = inline('sum( ( ( (x(1) + x(2).*log(x(3).*P1(:,1))) - P1(:,2)).^2)./P1(:,3))',1);options=foptions;options(14)=25000;params = fmins(errfn,init,options,[],data);return