function h = barerr( b, e, c )% BARERR  Bar plot with error bars%% h = barerr( b, e, c )% 07-Jun-99 -- created (RFM)defarg('c','br');h=bar(b,c(1));m=1.1*max(b+e);graphrange(NaN,NaN,NaN,m);n=size(b,2);for i=1:n,	set(line([ i i ],[ b(i)-e(i), b(i)+e(i) ]),'Color',c(2));	set(line([ i-0.05 i+0.05 ],[ b(i)-e(i), b(i)-e(i) ]),'Color',c(2));	set(line([ i-0.05 i+0.05 ],[ b(i)+e(i), b(i)+e(i) ]),'Color',c(2));endreturn