function graphrange( xmin, xmax, ymin, ymax )% GRAPHRANGE  Set limits of current axes.% set default argumentsdefarg('xmin',NaN);defarg('xmax',NaN);defarg('ymin',NaN);defarg('ymax',NaN);% get current graph rangexlim=get(gca,'XLim');ylim=get(gca,'YLim');% make and set new graph rangenewxlim = [ ifso(isnan(xmin),xlim(1),xmin), ifso(isnan(xmax),xlim(2),xmax) ];newylim = [ ifso(isnan(ymin),ylim(1),ymin), ifso(isnan(ymax),ylim(2),ymax) ];set(gca,'XLim',newxlim);set(gca,'YLim',newylim);return