function plotconsist(files,folder)% format:% *cond = [0.0625];% *cond1pc=1;cond1pcSE=2;cond1pa=3;cond1paSE=4;defarg('folder',pwd);if ~nargin,	[files,folders] = selectmanyfiles('psy');endif iscell(files),	% consistency plot	warning off;	figure;	mainaxis=axes('position',[0.1,0.1,0.8,0.8]);	leg=putlegend;	hold on;	numfiles = length(files);	ieratio = [];	ieratiostd = [];	for i = 1:numfiles,				data = readdata(files{i},folders{i});		ieratio(i) = fitie;		ieratiostd(i) = fitiestd;		threshold(i) = threshmean;		thresholdstd(i) = threshstd;		p = plot(data(:,cond1pa),data(:,cond1pc),[symbolcolor(i),plotsymbol(i),'-']);		leg = putlegend(leg,p,files{i});	end	putlegend(leg,2); 	putlegend(leg,2);	axis([0 100 0 100]);	axis square;	xlabel('Percent Agreement');	ylabel('Percent Correct');	titlenamestring = files{1};	title([titlenamestring(1:3),' Response Consistency']);	set(mainaxis,'XScale','log');	% i/e ratio plot	figure;	mainaxis1=axes('position',[0.1,0.1,0.8,0.8]);	subplot(1,2,1);	hold on;	leg2=putlegend;	p2 = barerr(ieratio,ieratiostd,'bk');	leg2 = putlegend(leg2,p2,[titlenamestring(1:3),' i/e']);%	p2 = errorbar([1:length(ieratio)],ieratio,ieratiostd,'r-*');%	leg2 = putlegend(leg2,p2(2),[titlenamestring(1:3),' i/e']);%	set(gca,'YScale','log');	putlegend(leg2,2);	axis([0 length(ieratio)+1 min(ieratio)/2 max(ieratio)*2]);	axis normal;	xlabel('Session');	ylabel('i/e noise standard deviation');	title([titlenamestring(1:3),' i/e ratio']);		% threshold plot	subplot(1,2,2);	hold on;	leg2=putlegend;	p2 = barerr(threshold,thresholdstd,'bk');	leg2 = putlegend(leg2,p2,[titlenamestring(1:3),' threshold']);% 	p2 = errorbar([1:length(threshold)],threshold,thresholdstd,'r-*');% 	leg2 = putlegend(leg2,p2(2),[titlenamestring(1:3),' threshold']);%	set(gca,'YScale','log');	putlegend(leg2,2);	axis([0 length(threshold)+1 min(threshold)/2 max(threshold)*2]);	axis normal;	xlabel('Session');	ylabel('Threshold');	title([titlenamestring(1:3),' Threshold']);	% echo to screen	% printstr(['ie ratio: ',num2str(ieratio)]);	% printstr(['ie ratiostd: ',num2str(ieratiostd)]);	warning on;end