function fftimage = plotfft2d(theimage)% function fftimage = plotfft2d(theimage)%% creates a 2d square Gaussian distribution matrix% % jan 6 2000  JMG U of Toronto Vision Labif nargin	x = ones(1,size(theimage,1))'*(-size(theimage,2)/2:size(theimage,2)/2-1);	y = -x';	z = zeros(size(x));	fftimage = fft2(theimage);	figure; imshow(scale(theimage)); title('The Image');	figure; image(x,y,z,abs(fftshift(fftimage))); title('Amplitude Spectrum'); zoom on;	figure; mesh(x,y,z,angle(fftshift(fftimage))); title('Phase Spectrum'); zoom on;	% 	figure; mesh(x,y,z,abs(fftshift(fftimage))); title('Amplitude Spectrum'); zoom on;% 	figure; mesh(x,y,z,angle(fftshift(fftimage))); title('Phase Spectrum'); zoom on;else	fftimage = nan;end	return