function plotthreshmixed(files,folder)defarg('folder',pwd);if ~nargin,	[files,folders] = selectmanyfiles('psy');endif iscell(files),				stretch = 2;	figure;		mainaxis1=axes('position',[0.1,0.1,0.8,0.8]);	set(mainaxis1,'XScale','log');	set(mainaxis1,'YScale','log');	spcounter = 0;	for i = 1:length(files),			% get data		data = readdata(files{i},folders{i});		% for zero condition display in log space		% zeroval = min([min(pos0conditions(find(pos0conditions~=0))),min(pos180conditions(find(pos0conditions~=0))),min(neg0conditions(find(pos0conditions~=0))),min(pos0conditions(find(neg180conditions~=0)))]);		zeroval = min([pos0intercept,pos180intercept,neg0intercept,neg180intercept]);		zeroval = zeroval/10;		% set up figure		if spcounter == 2,			figure;				mainaxis1=axes('position',[0.1,0.1,0.8,0.8]);			set(mainaxis1,'XScale','log');			set(mainaxis1,'YScale','log');			spcounter = 1;		else			spcounter = spcounter + 1;		end		subplot(1,2,spcounter);		axis normal;		hold on;				% initialize variables holding information for legend		leg=putlegend;				% plot thresholds		hold on;		pos0conditions(find(pos0conditions==0)) = zeroval;		e = errorbar(pos0conditions,pos0threshmean,pos0threshstd,[plotsymbol(1),symbolcolor(1)]);			 		pos0fitconditions = linspace(pos0conditions(1),pos0conditions(end),1000);		pos0fitdata = pos0intercept + pos0slope.*(pos0fitconditions);		leg=putlegend(leg,e(2),'pos-0');		e = plot(pos0fitconditions,pos0fitdata,[symbolcolor(1),'-']);		pos180conditions(find(pos180conditions==0)) = zeroval;		e = errorbar(pos180conditions,pos180threshmean,pos180threshstd,[plotsymbol(2),symbolcolor(1)]);			 		pos180fitconditions = linspace(pos180conditions(1),pos180conditions(end),1000);		pos180fitdata = pos180intercept + pos180slope.*(pos180fitconditions);		leg=putlegend(leg,e(2),'pos-180');		e = plot(pos180fitconditions,pos180fitdata,[symbolcolor(1),'-']);		neg0conditions(find(neg0conditions==0)) = zeroval;				e = errorbar(neg0conditions,neg0threshmean,neg0threshstd,[plotsymbol(3),symbolcolor(1)]);			 		neg0fitconditions = linspace(neg0conditions(1),neg0conditions(end),1000);		neg0fitdata = neg0intercept + neg0slope.*(neg0fitconditions);		leg=putlegend(leg,e(2),'neg-0');		e = plot(neg0fitconditions,neg0fitdata,[symbolcolor(1),'-']);		neg180conditions(find(neg180conditions==0)) = zeroval;						e = errorbar(neg180conditions,neg180threshmean,neg180threshstd,[plotsymbol(4),symbolcolor(1)]);			 		neg180fitconditions = linspace(neg180conditions(1),neg180conditions(end),1000);		neg180fitdata = neg180intercept + neg180slope.*(neg180fitconditions);		leg=putlegend(leg,e(2),'neg-180');		e = plot(neg180fitconditions,neg180fitdata,[symbolcolor(1),'-']);		set(gca,'XScale','log');		set(gca,'YScale','log');		conditionsmax(i) = max([pos0conditions,pos180conditions,neg0conditions,neg180conditions]);		conditionsmin(i) = min([pos0conditions,pos180conditions,neg0conditions,neg180conditions]);		threshmax(i) = max([pos0threshmean,pos180threshmean,neg0threshmean,neg180threshmean]);		threshmin(i) = min([pos0threshmean,pos180threshmean,neg0threshmean,neg180threshmean]);		% alter x-axis to range of data		axis([conditionsmin(i)/stretch conditionsmax(i)*stretch threshmin(i)/stretch threshmax(i)*stretch]);				allpos0slopes(i) = pos0slope;		allpos0intercepts(i) = pos0intercept/pos0slope;		allpos0slopesstd(i) = pos0slopestd;		allpos0interceptsstd(i) = allpos0intercepts(i).*sqrt((pos0interceptstd./pos0intercept)^2 + (pos0slopestd./pos0slope)^2);				allpos180slopes(i) = pos180slope;		allpos180intercepts(i) = pos180intercept/pos180slope;		allpos180slopesstd(i) = pos180slopestd;		allpos180interceptsstd(i) = allpos180intercepts(i).*sqrt((pos180interceptstd./pos180intercept)^2 + (pos180slopestd./pos180slope)^2);		allneg0slopes(i) = neg0slope;		allneg0intercepts(i) = neg0intercept/neg0slope;		allneg0slopesstd(i) = neg0slopestd;		allneg0interceptsstd(i) = allneg0intercepts(i).*sqrt((neg0interceptstd./neg0intercept)^2 + (neg0slopestd./neg0slope)^2);		allneg180slopes(i) = neg180slope;		allneg180intercepts(i) = neg180intercept/neg180slope;		allneg180slopesstd(i) = neg180slopestd;		allneg180interceptsstd(i) = allneg180intercepts(i).*sqrt((neg180interceptstd./neg180intercept)^2 + (neg180slopestd./neg180slope)^2);		% labels		warning off;	 	putlegend(leg,2);		xlabel('Condition');		ylabel('Threshold');		title([replacechar(files{i},'_','-'),' ',num2str(round(pos0threshpc*100)),'% Correct Thresholds']);		warning on;			end				% plot slope and intercept data		figure;	mainaxis1=axes('position',[0.1,0.1,0.8,0.8]);	titlenamestring = files{1};		% initialize variables holding information for legend	subplot(1,2,1);	leg=putlegend;	hold on;		% plot slopes	h = errorbar([1:length(allpos0slopes)],allpos0slopes,allpos0slopesstd,'k-*');				leg=putlegend(leg,h(2),[titlenamestring(1:3),' pos-0']);	h = errorbar([1:length(allpos180slopes)],allpos180slopes,allpos180slopesstd,'r-*');				leg=putlegend(leg,h(2),[titlenamestring(1:3),' pos-180']);	h = errorbar([1:length(allneg0slopes)],allneg0slopes,allneg0slopesstd,'g-*');				leg=putlegend(leg,h(2),[titlenamestring(1:3),' neg-0']);	h = errorbar([1:length(allneg180slopes)],allneg180slopes,allneg180slopesstd,'b-*');	leg=putlegend(leg,h(2),[titlenamestring(1:3),' neg-180']);		% labels 	putlegend(leg,2);	xlabel('Session');	ylabel('Slope');	title([titlenamestring(1:3),' Slopes']);	% alter axies to range of data	minslope = min([allpos0slopes,allpos180slopes,allneg0slopes,allneg180slopes]);	maxslope = max([allpos0slopes,allpos180slopes,allneg0slopes,allneg180slopes]);	axis([0 length(allpos180slopes)+1 minslope/stretch maxslope*stretch]);	set(gca,'YScale','log');	axis normal;				% initialize variables holding information for legend	subplot(1,2,2);	leg=putlegend;	hold on;	% plot intercepts	h = errorbar([1:length(allpos0intercepts)],allpos0intercepts,allpos0interceptsstd,'k-*');				leg=putlegend(leg,h(2),[titlenamestring(1:3),' pos-0']);	h = errorbar([1:length(allpos180intercepts)],allpos180intercepts,allpos180interceptsstd,'r-*');				leg=putlegend(leg,h(2),[titlenamestring(1:3),' pos-180']);	h = errorbar([1:length(allneg0intercepts)],allneg0intercepts,allneg0interceptsstd,'g-*');				leg=putlegend(leg,h(2),[titlenamestring(1:3),' neg-0']);	h = errorbar([1:length(allneg180intercepts)],allneg180intercepts,allneg180interceptsstd,'b-*');	leg=putlegend(leg,h(2),[titlenamestring(1:3),' neg-180']);		% labels	warning off; 	putlegend(leg,2);	xlabel('Session');	ylabel('X-Intercept');	title([titlenamestring(1:3),' X-Intercepts']);	% alter axies to range of data	minintercept = min([allpos0intercepts,allpos180intercepts,allneg0intercepts,allneg180intercepts]);	maxintercept = max([allpos0intercepts,allpos180intercepts,allneg0intercepts,allneg180intercepts]);	axis([0 length(allpos180intercepts)+1 minintercept/stretch maxintercept*stretch]);	set(gca,'YScale','log');	axis normal;	warning on;						end