function newleg = putlegend( leg, handle, label, position )% PUTLEGEND  Add keys to a plot legend%% newleg = putlegend( leg, handle, label, position )% 29/04/98 - created (RFM)defarg('position',Inf);if nargin==0,	leg=[];	leg.handles=[];	leg.labels=[];elseif nargin==1 | nargin==2,	defarg('handle',1); % serves as position indicator in this case	legend(leg.handles,leg.labels,handle);else	if position==Inf,		leg.handles = [ leg.handles ; handle ];		leg.labels = strvcat(leg.labels,label);	elseif position==-Inf,		leg.handles = [ handle; leg.handles ];		leg.labels = strvcat(label,leg.labels);	endend	newleg=leg;return