% make the averaged classification image data filesjg kandat;kansubs = {'ajr','amc','jmg'};kanconds = {'real','modal','amodal','mask','control'};nconds = length(kanconds);nsubs = length(kansubs);imtype = 'kaniza';kansz = 100;for i = 1:nconds	temps1r1 = zeros(kansz);	temps1r2 = temps1r1;	temps2r1 = temps1r1;	temps2r2 = temps1r1;	for j=1:nsubs		fname = [kansubs{j},imtype,kanconds{i},'src.mat'];		if exist(fname,'file')			load(fname);		else			s1r1 = zeros(kansz);			s1r2 = s1r1;			s2r1 = s1r1;			s2r2 = s1r1;				end		temps1r1 = temps1r1+s1r1;		temps1r2 = temps1r2+s1r2;		temps2r1 = temps2r1+s2r1;		temps2r2 = temps2r2+s2r2;			end	s1r1 = temps1r1;	s1r2 = temps1r2;	s2r1 = temps2r1;	s2r2 = temps2r2;			fname = ['ave',imtype,kanconds{i},'src.mat'];	save(fname,'s1r1','s1r2','s2r1','s2r2');endjg kaniza;