% make data figures for kaniza paperwhitespace = 20;kansz = 100;% the stored cell arrayskansubs = {'ajr','amc','jmg','ideal'};kanconds = {'real','modal','amodal','control'};% smoothed kaniza imagesnconds = length(kanconds);nsubs = length(kansubs);imtype = 'kaniza';bigimageclass = [];bigimageclasstol = [];bglum = 1;for j = 1:nsubs	tempimageclass = [];	tempimageclasstol = [];	for i = 1:nconds		tempimageclass = [tempimageclass,ones(kansz,whitespace)*bglum];		tempimageclasstol = [tempimageclasstol,ones(kansz,whitespace)*bglum];				% get the images		fname = [kansubs{j},imtype,kanconds{i},'.dat'];		if exist(fname,'file')			[class,classtol,rawclass] = doclass(fname,pwd,1,1);		else			class = ones(kansz);			classtol = ones(kansz);		end		tempimageclass = [tempimageclass,class];		tempimageclasstol = [tempimageclasstol,classtol];	end	tempimageclass = [tempimageclass,ones(kansz,whitespace)*bglum];		tempimageclasstol = [tempimageclasstol,ones(kansz,whitespace)*bglum];		bigimageclass = [bigimageclass;ones(whitespace,size(tempimageclass,2))*bglum];	bigimageclasstol = [bigimageclasstol;ones(whitespace,size(tempimageclasstol,2))*bglum];	bigimageclass = [bigimageclass;tempimageclass];	bigimageclasstol = [bigimageclasstol;tempimageclasstol];	end	smoothimage = [bigimageclass;ones(whitespace,size(tempimageclass,2))*bglum];tolimage = [bigimageclasstol;ones(whitespace,size(tempimageclasstol,2))*bglum];figure;imshow(smoothimage);figure;imshow(tolimage);imwrite(smoothimage,'Fig2.tiff');imwrite(tolimage,'Fig3.tiff');