function [dmat] = mkrdots(ndots,matsz,dotsz,bgcst,dotcst)defarg('ndots', 100);defarg('matsz',100);defarg('dotsz',5);defarg('bgcst',0);defarg('dotcst',1);dmat = zeros(matsz);circ = imcircle(dotsz)*dotcst;locs = randi(matsz-dotsz,[ndots,2]);for i = 1:ndots	tempdmat = dmat(locs(i,1):locs(i,1)+dotsz-1,locs(i,2):locs(i,2)+dotsz-1);	templocs = find(tempdmat==1);	tempcirc = circ;	tempcirc(templocs) = ones(size(templocs));		dmat(locs(i,1):locs(i,1)+dotsz-1,locs(i,2):locs(i,2)+dotsz-1) = tempcirc;endif bgcst	bglocs = find(dmat==0);	dmat(bglocs) = ones(size(bglocs))*bgcst;endreturn	