function [x,y] = getnormcdf(params,limits,rate,polarity)% function [x,y] = getnormcdf(delta,gamma,mu,sigma,limits,rate,polarity)%% Returns the x and y values for the parameters of a weibull function.% Defaults for the limits of the sample.if nargin < 2	limits(1) = 0;	limits(2) = 128;endif nargin < 3	rate = 1; endif nargin < 4	polarity = 1; enddelta = params(1);gamma = params(2);mu = params(3);sigma = params(4);x = linspace(limits(1),limits(2),abs(limits(2)-limits(1))*rate)';if polarity ~= -1	y = delta+gamma.*normcdf(x,mu,sigma);else	y = delta+gamma.*(1-normcdf(x,mu,sigma));endreturn;