function [reel] = mkdotmovie(sizeP,radiusP,shiftP,bgC,dotC)frame1 = zeros(sizeP);frame2 = frame1;dot = imcircle(radiusP*2)*dotC;dot1loc = [round(sizeP/2)-radiusP, round(sizeP/2)-floor(shiftP/2)-radiusP];dot2loc = [round(sizeP/2)-radiusP, round(sizeP/2)+round(shiftP/2)-radiusP];frame1(dot1loc(1):dot1loc(1)+radiusP*2-1,dot1loc(2):dot1loc(2)+radiusP*2-1) = dot;frame2(dot2loc(1):dot1loc(1)+radiusP*2-1,dot2loc(2):dot2loc(2)+radiusP*2-1) = dot;reel(:,:,1) = frame1;reel(:,:,2) = frame2;zerolocs = find(reel==0);reel(zerolocs) = ones(size(zerolocs))*bgC;f = fftunshift(makeGaussfilt(sizeP,radiusP*2,1,'l',0));reel(:,:,1) = real(ifft2(fft2(reel(:,:,1)).*f));reel(:,:,2) = real(ifft2(fft2(reel(:,:,2)).*f));return