function errorpsymet(blocks,threshlevel,gamma,delta,file,folder)% function errorpsymet(blocks,threshlevel,gamma,delta,file,folder)% look for file and folder namesif ~exist('file','var');	[file,folder] = uigetfile('*.dat','Choose File');enddefarg('folder',pwd);defarg('blocks',100);defarg('threshlev',0.75);defarg('gamma',0.5);defarg('delta',0.01);if file	% get data	data = readdata(file,folder);	 	% more than 1 condition	if exist('condition','var')		conditions = unique(data(:,condition));		numconditions = length(conditions);		for i =1:length(numconditions)			locs = find(data(:,condition)==conditions(i));			[ fits(i,:), stderr(i,:) ] = bootstrap( [data(locs,stimlevel),data(locs,correct)],'weibull', blocks, threshlev, gamma, delta);		end		% 1 condition	else				[ fits, stderr ] = bootstrap( [data(:,stimlevel),data(:,correct)],'weibull', blocks, threshlev, gamma, delta);	end	% save data	period=findstr(file,'.');	if isempty(period)==0,		file=file(1:period-1);	end	[nfile,nfolder] = uiputfile([folder,file,'.eff'],'Append to File');	if nfile				% matlab style		fid = fopen([nfolder,nfile],'a+');		savecommand(fid,['alphaSE = [',num2str(stderr(:,1)'),'];']);		savecommand(fid,['betaSE = [',num2str(stderr(:,2)'),'];']);		savecommand(fid,['threshSE = [',num2str(stderr(:,3)'),'];']);		fclose(fid);				% igor style		saveigor([file,'.igor'],folder,stderror(:,3),{[file(1:3),'threshSE']});					end		endreturn	