function doideal% for 10afc% approxthresh = [1e-8   5e-8   2.5e-07   1e-06   1e-05];% stimsize = 256;% numstim = 10;% showstim = [1:numstim];% % maxtrials = 250;% stimfile = 'FaceStruct.mat';% noiselevels = [0.0001     0.0005     0.0025     0.0125     0.0625];% numsessions = 1;% numstimlevels = 8;% numshift = [0	0	0	0	0];% logrange = 3;% rotation = 0;	% polarity = 'pos';% ieratio = 0;% endlabel = '';% idealeffnz(maxtrials,stimfile,numstim,noiselevels,approxthresh,numsessions,numstimlevels,numshift,logrange,showstim,rotation,polarity,stimsize,ieratio,endlabel)% for 2afcapproxthresh = [1e-8   5e-8   1e-07   5e-07   1e-05];showstim = [5 6];stimsize = 128;numstim = 2;maxtrials = 250;stimfile = 'FaceStruct.mat';noiselevels = [0.0001     0.0005     0.0025     0.0125     0.0625];numsessions = 1;numstimlevels = 8;numshift = [0	0	0	0	0];logrange = 3;rotation = 0;	polarity = 'pos';ieratio = 0;endlabel = '';idealeffnz(maxtrials,stimfile,numstim,noiselevels,approxthresh,numsessions,numstimlevels,numshift,logrange,showstim,rotation,polarity,stimsize,ieratio,endlabel)return