function [ thresh, i ] = idealthresh( sigmean, sigcov, threshlev, threshtol, init )% IDEALTHRESH  Find ideal observer's threshold%% thresh = idealthresh( sigmean, sigcov, threshlev, threshtol )% 07-Sep-98 -- created (RFM)% set default argumentsdefarg('threshlev',0.75);defarg('threshtol',0.005);defarg('init',1);% construct error functionerrfn=inline('mean(idealpcorr(sigmean,k*sigcov))-threshlev', ...	'k','sigmean','sigcov','threshlev');params={ sigmean, sigcov, threshlev };% find noise level yielding threshold[ thresh, i ]=findzero(errfn,params,0.1*init,10*init,threshtol);return