% the stored cell arrayskansubs = {'ajr','amc','jmg'};kanconds = {'real','modal','amodal','control'};kansz = 100;corr.conds = kanconds;nconds = length(kanconds);nsubs = length(kansubs);imtype = 'kaniza';% load the ideal images (stored as 'ideal')load idealtemplates;for j = 1:nsubs	eval(['corr.',kansubs{j},' = [];']);	for i = 1:nconds				fname = [kansubs{j},imtype,kanconds{i},'.dat'];		if exist(fname,'file')			[class,classtol,rawclass] = doclass(fname,pwd,1,1);						for k = 1:nconds								eval(['currcorr = sum(sum(rawclass.*ideal.',kanconds{k},'));']);				eval([kansubs{j},'corr.',kanconds{i},'(k) = currcorr;']);			end			else			eval([kansubs{j},'corr.',kanconds{i},' = [nan nan nan nan];']);		end	end	end	