function x = docmpgabor(funF,harF,funC,harC,funP,harP,szPIX,gaborsdPIX)% function x = docmpgabor(funF,harF,funC,harC,funP,harP,szPIX,gaborsdPIX)defarg('funF',2);defarg('harF',2*funF);defarg('funC',.1);defarg('harC',funC/3);defarg('funP',0);defarg('harP',0);defarg('szPIX',100);defarg('gaborsdPIX',szPIX/4);fun = docosimage(funF,funC,funP,szPIX);har = docosimage(harF,harC,funP*harF+harP,szPIX);gabor = dogauss2d(szPIX,gaborsdPIX);x = (fun+har).*gabor;return;