function cross = makecross(lengthp,heightp,widthp,bgcst,fgcst)defarg('lengthp', 50);defarg('heightp', 50);defarg('widthp', 20);defarg('bgcst', 0);defarg('fgcst', 1);cross = ones(heightp,lengthp).*bgcst;cross(1:end,ceil(lengthp/2)-floor(widthp/2)+1:ceil(lengthp/2)+ceil(widthp/2)) = ones(heightp,widthp)*fgcst;cross(ceil(heightp/2)-floor(widthp/2)+1:ceil(heightp/2)+ceil(widthp/2),1:end) = ones(widthp,lengthp)*fgcst;return