% makelpnoise.mdefarg('sz',256);defarg('n',10);% used for bp noise in eqnoise experimentf1 = 2;f2 = 4;if 0seeds.s1 = [3.5202;0.5587].*1e+09;seeds.s2 = [1.6905;2.2646].*1e+09;seeds.s3 = [4.1881;1.2258].*1e+09;seeds.s4 = [1.4628;3.5060].*1e+09;seeds.s5 = [3.2149;0.1674].*1e+09;seeds.s6 = [4.2544;1.8685].*1e+09;seeds.s7 = [4.2630;0.1891].*1e+09;seeds.s8 = [0.1220;3.3983].*1e+09;seeds.s9 = [3.7445;0.9920].*1e+09;seeds.s10 = [3.2298;1.2934].*1e+09;endseeds = [.8;2.1].*1e+09;randn('state',seeds);filt = makeidealfilt(sz,f1,f2,0);for i = 1:n	%eval(['randn(''state'',seeds.s',num2str(i),');']);	nz = cliprandn(sz,2);	eval(['images.nz',num2str(i),' = real(ifft2(fft2(nz).*fftunshift(filt)));']);	eval(['images.nz',num2str(i),' = images.nz',num2str(i),'* sqrt(.1/stdm(images.nz',num2str(i),')^2);']); 	figure; eval(['imshow(scale(images.nz',num2str(i),'));']);endtilefigs;return;