load facestruct;cal = getddf('AppleA.ddf');%freq = [1 2 4 8 16 32 64 128];freq = [1];names = fieldnames(images);vmax = zeros(length(names),length(freq));cmax = min(abs([(cal.lmaxminave(1)-cal.lmaxminave(3))/cal.lmaxminave(3),(cal.lmaxminave(2)-cal.lmaxminave(3))/cal.lmaxminave(3)])); for i = 1:length(freq)	if freq(i) ~= 1		filt = fftunshift(makeidealfilt(256,freq(i)/2,freq(i),0));	else		filt = ones(256);	end		for j = 1:length(names)		eval(['temp = images.',names{j},';']);		temp = real(ifft2(fft2(temp).*filt));		temp = temp*(cmax/max(max(temp)));		vmax(j,i) = std(temp(:))^2;			endend%save maxfacevars vmax freq;