function [c] = mkcross(heightP,widthP,crossheightP,crosswidthP,bgC,fgC)defarg('heightP',400);defarg('widthP',400);defarg('crossheightP',100);defarg('crosswidthP',100);defarg('bgC',0);defarg('fgC',1);c = ones(heightP,widthP).*bgC;rowcenter = floor(heightP/2);colcenter = floor(widthP/2);crossrowcenter = floor(crossheightP/2);crosscolcenter = floor(crosswidthP/2);rowinc = rem(crossheightP,2)==0;colinc = rem(crosswidthP,2)==0;c(rowcenter-crossrowcenter+rowinc:rowcenter+crossrowcenter,:) = ones(size(c(rowcenter-crossrowcenter+rowinc:rowcenter+crossrowcenter,:)))*fgC;c(:,colcenter-crosscolcenter+colinc:colcenter+crosscolcenter,:) = ones(size(c(:,colcenter-crosscolcenter+colinc:colcenter+crosscolcenter,:)))*fgC;return