function [w] = mkwubbi(bigradP,littleradP,npadP,bgC,fgC)defarg('bigradP',100);defarg('littleradP',30);defarg('bgC',0);defarg('fgC',1);defarg('npadP',5);% make top left cornerw = imcircle(bigradP);w = pad(w,[npadP,npadP,npadP,npadP],0);w(1:littleradP,1:littleradP) = w(1:littleradP,1:littleradP)+imcircle(littleradP);fglocs = find(w > 0);bglocs = find(w == 0);w(fglocs) = ones(size(fglocs))*fgC;w(bglocs) = ones(size(bglocs))*bgC;w = w(1:floor(size(w,1)/2),1:floor(size(w,2)/2));% make complete figurew = [w,fliplr(w)];w = [w;flipud(w)];return;