function [w] = mkwubbiOCC(bigradP,littleradP,npadP,bgC,fgC,stretchLR,stretchUD,rotation,occszP,occC,offsetP,blur,blurfreq,bluroctaves)defarg('bigradP',100);defarg('littleradP',30);defarg('bgC',0);defarg('fgC',1);defarg('stretchLR',10);defarg('stretchUD',0);defarg('npadP',5);defarg('occszP',round(bigradP*1.25));defarg('occC',.5);defarg('blur',0);defarg('blurfreq',bigradP/5);defarg('bluroctaves',1.5);defarg('rotation',0);   % -45 for horizontal figure; 0 for diagonal figuredefarg('offsetP',0);% increase size to increase resolutionexpandfactor = 3;bigradP = bigradP*expandfactor;littleradP = littleradP*expandfactor;stretchLR = stretchLR*expandfactor;stretchUD = stretchUD*expandfactor;npadP = npadP*expandfactor;occszP = occszP*expandfactor;blurfreq = blurfreq*expandfactor;offsetP = offsetP*expandfactor;w = mkwubbi(bigradP,littleradP,npadP,bgC,fgC);w = placewubbiOCC(w,occszP,occC,bgC,stretchLR,stretchUD,rotation,blur,blurfreq,bluroctaves,offsetP);% shrink to 1/2 sizew = imresize(w,1/expandfactor);% bgsz = bigradP*2+npadP*2+max([stretchLR,stretchUD])*2+littleradP*2%bg = ones(bigradreturn;