function [m] = pad(m,tlrb,n)% function [m] = pad(m,tlrb,n)%% pad a matrix with value (n).% TLRB is the number of rows and columns% to append to the top, left, right and bottom% of the matrix, respectively.%% Jan 13 2000   JMGif nargin > 1		defarg('n',0);	if length(tlrb) ~= 4		error('tlrb requires 4 values.');	else				for i = 1:4			if tlrb(i) ~= 0				sz = size(m);				if i == 1					m = [ones(tlrb(i),sz(2)).*n;m];				elseif i == 2					m = [ones(sz(1),tlrb(i)).*n',m];				elseif i == 3					m = [m,ones(sz(1),tlrb(i)).*n'];				elseif i == 4					m = [m;ones(tlrb(i),sz(2)).*n];				end			end		end	end% 		l = ones(sz(1),tlrb(2)).*n'% 		r = ones(sz(1),tlrb(3)).*n'% 		t = ones(tlrb(1),sz(2)+tlrb(2)+tlrb(3)).*n% 		b = ones(tlrb(4),sz(2)+tlrb(2)+tlrb(3)).*n% 		r = ones(nrows,sz(2))*n';% 		c = (ones(sz(1)+nrows*2,ncols)*n);% 		p = [c,[r;m;r],c];% 		p = [l,[t;m;b],r];else	error('not enough arguments passed.');endreturn