function [output] = scalearb(input,themin,themax)% SCALEARB% function [output] = scalearb(input,themin,themax)%% scales data between arbitrary limits [themax, themin].% default limits are [1,0].%% march 22 JMG%% oct 18 1999 fixed to handle N-D matrices (JMG)defarg('themin',0);defarg('themax',1);if exist('input','var')	slope = (themax-themin)/(max(input(:))-min(input(:)));					intercept = themax - slope*(max(input(:)));						output = input.*slope + intercept;else	printstr('Requires input matrix.');	help scalearb;endreturn;