function [output] = scalearbND(input,themin,themax)% function [output] = scalearbND(input,themin,themax)%% scales ND data between arbitrary limits [themax, themin].% default limits are [1,0].%% march 22 JMGdefarg('themin',0);defarg('themax',1);if exist('input','var')	slope = (themax-themin)/(max(input(:))-min(input(:)));					intercept = themax - slope*(max(input(:)));						output = input.*slope + intercept;else	printstr('Requires input matrix.');	help scalearbND;endreturn;