function [theimage] = shiftImage(theimage,voff,hoff)% shifts the position of an image. wraps around the sides.% voff is the vertical offset, hoff the horizontal.% % July 1998  JMG U of T Vision Lab% vertical shiftif voff ~= 0	if voff < 0			temp1 = theimage(:,1:abs(voff));		temp2 = theimage(:,abs(voff)+1:end);		theimage = [temp2,temp1];	else		temp1 = theimage(:,end-voff:end);		temp2 = theimage(:,1:end-voff-1);		theimage = [temp1,temp2];	endend% horizontal shiftif hoff ~= 0	if hoff < 0			temp1 = theimage(1:abs(hoff),:);		temp2 = theimage(abs(hoff)+1:end,:);		theimage = [temp2;temp1];	else		temp1 = theimage(end-hoff:end,:);		temp2 = theimage(1:end-hoff-1,:);		theimage = [temp1;temp2];	endendreturn;