function newlist = addtslist( list, varargin )% ADDTSLIST  Add trial sources to a trial list.%% newlist = addtslist( list, src1, ... )% 28-Jan-98 -- created (RFM)newlist=list;newlist.sources=[ list.sources varargin ];newlist.nsources=size(newlist.sources,2);newlist.active=ones(1,newlist.nsources);newlist.iperm=1;newlist.perm=randperm(newlist.nsources);newlist.prevsource=[];return