function trialsrc = mktslist( varargin )% MKTSLIST  Make a list of trial sources.%% trialsrc = mktslist( src1, ... )% % 28-Jan-98 -- created (RFM)trialsrc.type='list';             % list typetrialsrc.sources=varargin;        % list of trial sourcestrialsrc.nsources=nargin;         % number of trial sourcestrialsrc.active=ones(1,trialsrc.nsources);  % 'active' flag for each sourcetrialsrc.iperm=1;                 % permuted index of next source to polltrialsrc.perm=randperm(trialsrc.nsources); % permutation of levelstrialsrc.prevsource=[];           % index of last source polledtrialsrc.trial=0;return