function m = placecircle(n,radP,xP,yP,DC)defarg('n',100);defarg('radP',10);defarg('xP',20);defarg('yP',20);defarg('DC',1);m = zeros(n);if rem(n,2),	center = round(n/2);else	center = n/2 + 1;endcoords = [center+xP-radP,center-yP-radP];circle = imcircle(radP*2);if max(coords+(radP*2-1)) > n | min(coords) < 1	error('circle falls outside of square.');else	m(coords(2):coords(2)+radP*2-1,coords(1):coords(1)+radP*2-1) = circle;endif DC,	m(center,center) = 1;end	return;