mainscrs=1; % this is the screen upon which the image is to be displayed.swidth=1024; sheight=768; pixelsize=8; hz=85;% should be the same as settings during calibration; should be stored with calibration file, but currently are not%swidth=1600; sheight=1024; pixelsize=8; hz=60;% should be the same as settings during calibration; should be stored with calibration file, but currently are notres=nearestresolution(mainscrs,swidth,sheight,hz,pixelsize);scrinfo=screen(mainscrs,'resolution',res);displayrate=framerate(mainscrs); % frame-rate of the monitor in Hertzdefarg('calfile','Billie1024x768_256col.DDF');	% the name of the calibration filedefarg('exptname','trials_mouse');	% the name of the experimenttrialsmousesetpaths; % set folder names and paths for data files% file & directory names names% defarg('datafilename',['testdata.txt']);% defarg('mousedatafilename',['mousedata.txt']);% defarg('pmMOUSEfolder',['pmMOUSEfiles']);	% N.B. make sure this folder exists within the folder containing 'pmlab'!% defarg('pmTEXTfolder',['pmTEXTfiles']);	% N.B. make sure this folder exists within the folder containing 'pmlab'!% defarg('pmMATfolder',['pmMATfiles']);	% N.B. make sure this folder exists within the folder containing 'pmlab'!% % set the paths% disp('setting path names...');% mfilename='pmlab'; mainfile = [mfilename,'.m'];% eval(['mainpath=which(',quotestring(mainfile),');']);% mainpath = mainpath(1:end-length(mainfile));% matdatadir=[mainpath,pmMATfolder,':'];% mousedatadir=[mainpath,pmMOUSEfolder,':'];% textdatadir=[mainpath,pmTEXTfolder,':'];% cd(mainpath);% temporal durations,in secondsdefarg('duration',0.5);	% spot duration in secondsdefarg('intertrial',2.0);	% inter-trial interval in seconds; meaninful only when usespace=0 (see below)defarg('adaptseconds',5);	% adaptation time% isidurframes=[4,32,64,128,256,512,768];isidurframes=[2,16,32,64,128,256,512];numisi=length(isidurframes);% stimulus dimensionsdefarg('vd',114);			% viewing distance in cm; not used in current programdefarg('spotdiampix',16);	% diameter of spot target, in pixelsdefarg('circdiampix',323);	% diameter of circle, in pixelsdefarg('practicetrials',5); % number of practice mouseclickstarglocsdeg=[0,30,60,90,120,150,180,210,240,270,300,330];	% target locations; should be separated by 2*randrangedeg (see next line)randrangedeg=15;	% randomization range (i.e., location ± 15 deg); N.B. this parameter must be coordinated with targlocsdeg to cover the entire 360 deg range of directionsnumlocs=length(targlocsdeg);defarg('numtrialsperlocation',1); % # of trials per target location for each isi% subject informationdefarg('usespace',0);	% when zero, trials are started automatically; when 1, trials started by clicking mouse near fixation pointdefarg('distanceFromFixPnt',16);	% # of pixels from fixation point that the mouse must be clicked to start a trial; valid only when 'usespace' is 1defarg('groupID','x');	% identifies group o=old; y=young; x=not setcd(mainpath); load nextidnum; defarg('sidnum',num2str(nextidnum)); nextidnum=nextidnum+1; save ('nextidnum','nextidnum'); clear nextidnum;defarg('sgender',[]);defarg('sage',-1);defarg('scomment',[]);