function [Task] = choosedevice(Task_On,One,Two,Three) % See the Choose_Block m-file for details. Works the same way. Perhaps% in the future I will merge these two into one unit. The different% numbers of choices makes it more complicated than having two separate% routines. this could be done easily in Mtlb 5.1 with vargin.%% October 17, 1996		J Gold	P J Bennett University of Toronto Vision Lab   %% Fixed for MATLAB 5.1  JMG   January 30, 1998if strcmp(get(Task_On,'Checked'),'off')		set([One Two Three],'Checked','off');	set(Task_On,'Checked','on'); 	temp_state = 1;else set(Task_On,'Checked','off');	set(One,'Checked','on'); 	temp_state = 0;end% Assign the state.if temp_state == 1	if Task_On == One		Task = 0;	elseif Task_On == Two		Task = 1;	elseif Task_On == Three		Task = 2;	end	else	Task = 0;	end% END.