function calFitRecord=pbFitBITSData(calRecord)    caldata=calRecord.caldata;    bitsNumbers = caldata(:,8);    lumValues = caldata(:,5);	% initial parameters	alpha = min(min(caldata(:,5)));	beta = .01;	kappa = .01;	gamma = 2;	initcoeffs = [alpha beta kappa gamma];	% fits    coeffs = pbPelliFit(bitsNumbers,lumValues,initcoeffs);	bn = 0:pbMaxBITSmono;    bn = bn';    rgb = pbBits2MonoRGB(bn);    L = pbPelliPower(coeffs,bn);	% final calibration plot	plot(bn,L,'k-');	ylabel('Luminance (cd/m^2)');	xlabel('Index');	title('Look-Up Table');				drawnow;            calFitRecord=calRecord;    calFitRecord.ABKG=coeffs;    calFitRecord.calmatrix=[rgb,L,bn];		end