function theClut=pbMakeCLUT(s,lowL,highL,lowIndex,highIndex)%%%%% get fields from scalmatrix = s.calmatrix;P = s.pmaxminave;L = s.lmaxminave;ABKG = s.ABKG;displayLum=calmatrix(:,4);tmp=displayLum;Lum=linspace(lowL,highL,(highIndex-lowIndex+1));theClut=zeros(256,3);clutindex=lowIndex;for n=1:length(Lum)	tmp=abs(displayLum-Lum(n)); % subtract desired luminance from calibrated luminances and take absolute value	[y,k]=min(tmp); % find the minimum (at location k); this is the luminance that is closest to the desired value	theClut(clutindex,1:3)=calmatrix(k,1:3); % store that RGB value in the CLUT	clutindex=clutindex+1;end;return;