function outimage=highpass(f,inimage,showImage);dim=size(inimage,1);dim2=size(inimage,2);if (dim ~= dim2) return; end;ft=fftshift(fft2(inimage));amp=abs(ft);phase=angle(ft);r=size(amp,1);c=size(amp,2);dc=[round(r/2)+1,round(c/2)+1];dcAmp=amp(dc(1),dc(2));filter2d=zeros(dim,dim);% for xk=1:dim% 	for yk=1:dim% 		dx=dc(1)-xk;% 		dy=dc(2)-yk;% 		d=sqrt(dx*dx + dy*dy);% 		if (d > f)% 			filter2d(xk,yk)=1;% 		end;% 	end;% end;dx=1:dim;dx=dx-dc(1);for yk=1:dim	dy=dc(2)-yk;	d=sqrt(dx.*dx + dy*dy);	val1=find((d > f));	filter2d(yk,val1)=ones(1,length(val1));end;filter2d(dc(1),dc(2))=1;amp = amp.*filter2d;ft = amp.*exp(sqrt(-1)*phase);outimage=real(ifft2(fftshift(ft)));if (showImage ~= 0)	figure(1);imshow(scale(amp));set(1,'Name','filter')	figure(2);imshow(scale(inimage));set(2,'Name','input')	figure(3);imshow(scale(outimage));set(3,'Name','output')end;