pixmax=254;pixmin=2;filetype='bmp';tmp=0:255;myclut=tmp'*[1,1,1];for jj=1:nfilters	eval(['tmppixels = stimfaces',num2str(jj),';']);	tmppixels=round(127+127.*tmppixels);	for kk=1:nfaces		tmpface=tmppixels(:,:,kk);		toohigh=find(tmpface>pixmax); clip=pixmax+zeros(size(toohigh));tmpface(toohigh)=clip;		toolow=find(tmpface<pixmin); clip=pixmin+zeros(size(toolow));tmpface(toolow)=clip;		tmppixels(:,:,kk)=uint8(tmpface);% 		drawnow;% 		waitsecs(1);	end;% 	eval(['pixelfaces',num2str(jj),'=tmppixels;']);	for nn=1:nfaces		tmpface=tmppixels(:,:,nn);		filename=['F',num2str(nn),'upNOTCH',num2str(cutoff(jj,1)),'.',filetype];		imwrite(tmpface,myclut,filename,filetype);	end;end;% for jj=1:nfilters% 	eval(['imagesource = pixelfaces',num2str(jj),';']);% 	for kk=1:nfaces% 		tmpface=imagesource(:,:,kk);% 		filename=['F',num2str(kk),'upFIL',num2str(jj),'.',filetype];% 		imwrite(tmpface,myclut,filename,filetype);% 	end;% end;% 