function theImage=circle(nr);%% function theImage=circle(nr);%% nc=nr;% r=nr/2;% x=1:nc;x=x-r;% x=ones(nr,1)*x;% y=x';% theImage=r-sqrt(x.^2 + y.^2);% tloc=find(theImage>=0);% theImage(tloc)=ones(1,length(tloc));% tloc=find(theImage<0);% theImage(tloc)=zeros(1,length(tloc));theImage=gauss(nr);tloc=find(theImage>=0.043);theImage(tloc)=ones(1,length(tloc));tloc=find(theImage<1);theImage(tloc)=zeros(1,length(tloc));return;