function theImage=glasspatterns(nr,numdots,c,dx,dy,bkgrnd);%%%	if (nr<1)		disp('illegal value for # rows!');	end;	if (numdots<1)		disp('illegal value for # of dots!');	end;	if (length(c)~=length(dx))|(length(c)~=length(dy))		disp('lvectors c, dx, and dy must have equal lengths!');	end;	ds=4;	de=10;	nc=nr;	theImage=bkgrnd+zeros(nr,nc);	numpoints=nr*nc;	[xloc,yloc]=randcoords(nr,nc,de,numdots);	dotloc=xy21d(xloc,yloc,nc);	%dotloc=1+floor(numpoints*rand(1,numdots));	k1=length(c);	for k0=1:k1		xloc=xloc+dx(k0); xloc=1+mod(xloc,nc);		yloc=yloc+dy(k0); yloc=1+mod(yloc,nr);		dotloc=xy21d(xloc,yloc,nc);		thedots=c(k0)+zeros(1,numdots);		xtemp=xloc;		ytemp=yloc;		for c0=1:ds			for c1=1:ds				theImage(dotloc)=theImage(dotloc)+thedots;				xtemp=xtemp+1; xtemp=1+mod(xtemp,nc);				dotloc=xy21d(xtemp,ytemp,nc);			end;			xtemp=xloc;			ytemp=ytemp+1; ytemp=1+mod(ytemp,nr);			dotloc=xy21d(xtemp,ytemp,nc);		end;			end;	return;		