function theImage=pbSpot(nr,r,polarity);%% function theImage=pbSpot([rows],[radius],[polarity]);%if (nargin<1)	nr=256;	r=128;	polarity=1;end;if (nargin<2)	r=round(nr/2);	polarity=1;end;if (nargin<3)	polarity=1;end;% first, create an x,y gridnc=nr;[x,y]=meshgrid(0:(nc-1),0:(nr-1));c=round(nr/2);x=x-c;y=y-c;% now, draw the spotrtmp=r*r;if polarity==1	theImage=(((x.*x)+(y.*y))<=rtmp);else	theImage=(((x.*x)+(y.*y))>=rtmp);end;return;