function [x,y]=randcoords(nr,nc,de,numdots);%%	function [x,y]=randcoords(nr,nc,de,numdots);%	nr == number of rows%	nc == number of columns%	de == min distance between a dot and the edge of [nr,nc] matrix%	numdots == # of dots%	returns x == x (column) coordinates%	returns y == y (row) coordinates%if ((nr<1)|(nc<1))	disp('illegal # rows and/or # columns!');	return;end;if (numdots<0)|(numdots>(nr*nc))	disp ('illegal # of dots!');	return;end;	k0=de*2;	x=de+floor((nc-k0)*rand(1,numdots));	y=de+floor((nr-k0)*rand(1,numdots));	return;